#!/sbin/runscript

depend() {
  （依赖关系信息）
}

start() {
  （启动服务所必需的命令）
}

stop() {
  （停止服务所必需的命令）
}

restart() {
  （重启服务所必需的命令）
}

depend() {
  need net
  use logger dns
  provide mta
}

depend() {
  need net
  before inetd
  before xinetd
}

start() {
  ebegin "Starting my_script"
  start-stop-daemon --start --exec /path/to/my_script \
    --pidfile /path/to/my_pidfile --name foo
  eend $?
}

opts="${opts} restartdelay"

restartdelay() {
  stop
  sleep 3    #在再次启动前等待3秒钟
  start
}
