body.pre:
# 让dhcpd禁用动态DNS.
# 没有这个dhcpd会拒绝启动
ddns-update-style none;

# 创建一个子网：
subnet 192.168.10.0 netmask 255.255.255.0 {
  # 我们的启动客户端的地址池。不要忘记了“动态bootp”！
  pool {
    range dynamic-bootp 192.168.10.1 192.168.10.254;
  }

  # DNS服务器和默认网关——按实际情况替换相应的值
  option domain-name-servers 203.1.72.96, 202.47.56.17;
  option routers 192.168.10.1;

  # 告诉DHCP服务器对于这个子网来说它是有权威性的。
  authoritative;

  # 允许在这个子网上使用BOOTP
  allow bootp;
}

subnet xxx.xxx.xxx.xxx netmask xxx.xxx.xxx.xxx {
  # ……通用的内容在这里……

  # SGI工作站……把'sgi'改成你的SGI机器的主机名
  host sgi {

    # SGI机器的MAC地址。通常这个地址写在机器的
    # 背面或底部。
    hardware ethernet 08:00:69:08:db:77;

    # 供下载的TFTP服务器（默认的，和DHCP服务器是同一台机器）
    next-server 192.168.10.1;

    # 要分配给SGI机器的IP地址
    fixed-address 192.168.10.3;

    # PROM要下载和启动的文件名
    filename "/gentoo-r4k.img";
  }
}

subnet xxx.xxx.xxx.xxx netmask xxx.xxx.xxx.xxx {
  # ……通用的内容在这里……

  # 一个Cobalt服务器的配置
  # 这里设定hostname：
  host qube {
    # nfsroot目录的路径
    # 主要是在CoLo上使用TFTP启动参数时用到的
    # 你不应该改动这个。
    option root-path "/nfsroot";

    # Cobalt服务器的以太网MAC地址
    hardware ethernet 00:10:e0:00:86:3d;

    # 下载镜像所用的服务器
    next-server 192.168.10.1;

    # cobalt服务器的IP地址
    fixed-address 192.168.10.2;

    # 相对于/nfsroot的default.colo文件的位置
    # 你不应该改动这个。
    filename "default.colo";
  }
}
